mapper.insert:
insert into free(
	free_no, free_writer, free_title, free_content, free_date, free_age,
	free_ground, free_gender, free_size, free_fee, free_skill
)
values(
	#{freeNo}, #{freeWriter}, #{freeTitle}, #{freeContent}, #{freeDate}, #{freeAge},
	#{freeGround}, #{freeGender}, #{freeSize}, #{freeFee}, #{freeSkill}
)
